import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const FahrenheitInput = (props) =>{



  const fahrenheitChange = (event) =>{
    props.updateFahrenheit(event.target.value)
  }

  return(
    <label>
      Fahrenheit
      <input 
      type="Number" 
      id="fahrenheit"
      value = {props.f}
      onChange={(event) => fahrenheitChange}
      />
    </label>
  );
}
const CelsiusInput = () =>{

  return(
    <label>
      Celsius
      <input 
      type="Number" 
      id="celsius"
      />
    </label>
  );
}

class App extends React.Component{
  render(){

    const onChangeFahrenheit = (f_value) =>{
      this.setState({f: f_value})
    }

    state={
      f: 0,
      c: 0
    }
    return(
      <div>
        <h2>
          Temperature Conversion
        </h2>
        <FahrenheitInput {...this.state} updateFahrenheit={this.onChangeFahrenheit}/>
        <CelsiusInput/>
      </div>
    );
    }
}


ReactDOM.render(
    <App />,
  document.getElementById('root')
);
