import React, { useState } from 'react';
import ReactDOM from 'react-dom';

const FahrenheitInput = (props) =>{

  return(
    <label>
      Fahrenheit
      <input 
      type="Number" 
      id="fahrenheit"
      value = {props.f}
      onChange={event => props.updateFahrenheit(event.target.value)}
      />
    </label>
  );
}
const CelsiusInput = (props) =>{

  return(
    <label>
      Celsius
      <input 
      type="Number" 
      id="celsius"
      value = {props.c}
      onChange={event => props.updateCelsius(event.target.value)}
      />
    </label>
  );
}

class App extends React.Component{
  state={
    f: "",
    c: ""
  };

  onChangeFahrenheit = (f_value) =>{
    const c_value = (f_value - 32) * 5/9;
    this.setState({c: c_value, f:f_value});
  }

  onChangeCelsius = (c_value) =>{
    const f_value = c_value * (9/5) + 32;
    this.setState({c: c_value, f:f_value});
  }

  render(){

    return(
      <div>
        <h2>
          Temperature Conversion
        </h2>
        <FahrenheitInput {...this.state} updateFahrenheit={this.onChangeFahrenheit}/>
        <CelsiusInput {...this.state} updateCelsius={this.onChangeCelsius}/>
      </div>
    );
    }
}


ReactDOM.render(
    <App />,
  document.getElementById('root')
);
