import React, { useState } from "react";

export default function Button({ number, query, setQuery }) {
  let compute_result = () => {};

  let updateText = (ev) => {
    if (ev.target.value == "=") {
      compute_result();
    } else {
      if (query == 0) {
        setQuery(() => ev.target.value);
      } else {
        setQuery((prev_value) => String(prev_value) + ev.target.value);
      }
    }
  };

  return (
    <>
      <input
        type="button"
        value={number}
        onClick={(ev) => updateText(ev)}
      ></input>
    </>
  );
}
