import React, { useState } from "react";
import ReactDOM from "react-dom";

function Celsius({ c, update_c }) {
  return (
    <div>
      <label htmlFor="celsius">Celsius</label>
      <input type="number" name="celsius" id="celsius" value={c} />
    </div>
  );
}

function Fahrenheit({ f, update_f }) {
  return (
    <div>
      <label htmlFor="fahrenheit">Fahrenheit</label>
      <input type="number" name="fahrenheit" id="fahrenheit" value={f} />
    </div>
  );
}

function App() {
  const [f, update_f] = useState(0);
  const [c, update_c] = useState(0);
  return (
    <div>
      <Celsius c={c} update_f={update_c} />
      <Fahrenheit f={f} update_f={update_f} />
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
