import React, { useState } from "react";
import ReactDOM from "react-dom";

function Celsius({ c, update_c, update_f }) {
  function onChangeCelsius(c_value) {
    update_c(c_value);
    const f_value = c_value * (9 / 5) + 32;
    update_f(f_value);
  }
  return (
    <div>
      <label htmlFor="celsius">Celsius</label>
      <input
        type="number"
        name="celsius"
        id="celsius"
        value={c}
        onChange={(ev) => onChangeCelsius(ev.target.value)}
      />
    </div>
  );
}

function Fahrenheit({ f, update_f, update_c }) {
  function onChangeFahrenheit(f_value) {
    update_f(f_value);
    const c_value = ((f_value - 32) * 5) / 9;
    update_c(c_value);
  }
  return (
    <div>
      <label htmlFor="fahrenheit">Fahrenheit</label>
      <input
        type="number"
        name="fahrenheit"
        id="fahrenheit"
        value={f}
        onChange={(ev) => onChangeFahrenheit(ev.target.value)}
      />
    </div>
  );
}

function App() {
  const [f, update_f] = useState(0);
  const [c, update_c] = useState(0);

  return (
    <div>
      <Celsius c={c} update_c={update_c} update_f={update_f} />
      <Fahrenheit f={f} update_c={update_c} update_f={update_f} />
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
