import React, { useState } from "react";
import ReactDOM from "react-dom";

function Celsius() {
  return (
    <div>
      <label for="celsius">Celsius</label>
      <input type="number" name="celsius" id="celsius" />
    </div>
  );
}

function Fahrenheit(f, update_f) {
  return (
    <div>
      <label for="fahrenheit">Fahrenheit</label>
      <input type="number" name="fahrenheit" id="fahrenheit" />
    </div>
  );
}

function App() {
  const [f, update_f] = useState(0);
  const [c, update_c] = useState(0);
  return (
    <div>
      <Celsius />
      <Fahrenheit f={f} update_f={update_f} />
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
